def make_a_pile(n):
    pile = [n]
    for i in range(1, n):
        if n % 2 == 0:
            pile.append(n + i)
        else:
            pile.append(n + 2*i)
    return pile

print(make_a_pile(3)) # [3, 5, 7]
print(make_a_pile(4)) # [4, 6, 8, 10]
print(make_a_pile(5)) # [5, 7, 9, 11, 13]

def get_max_triples(n):
    a = [i * i - i + 1 for i in range(1, n+1)]
    count = 0
    for i in range(n-2):
        for j in range(i+1, n-1):
            for k in range(j+1, n):
                if (a[i] + a[j] + a[k]) % 3 == 0:
                    count += 1
    return count

print(get_max_triples(5)) # Output: 1

def tri(n):
    if n == 0:
        return []
    elif n == 1:
        return [3]
    else:
        seq = [3, 1]
        for i in range(2, n+1):
            if i % 2 == 0:
                seq.append(1 + i//2)
            else:
                seq.append(seq[i-1] + seq[i-2] + seq[i+1])
        return seq

def select_words(s, n):
    vowels = ['a', 'e', 'i', 'o', 'u']
    words = s.split()
    result = []
    for word in words:
        consonants = 0
        for letter in word:
            if letter.lower() not in vowels:
                consonants += 1
        if consonants == n:
            result.append(word)
    return result

def even_odd_palindrome(n):
    even_count = 0
    odd_count = 0
    for i in range(1, n+1):
        if str(i) == str(i)[::-1]:
            if i % 2 == 0:
                even_count += 1
            else:
                odd_count += 1
    return (even_count, odd_count)
